INSERT INTO `product` VALUES (1,1,'Quần âu','<p>&bull; L&agrave; item kh&ocirc;ng thể thiếu trong tủ đồ v&igrave; sự thoải m&aacute;i, dễ chịu, lại rất dễ phối đồ.<br />\n&bull; &Aacute;o thun unisex th&iacute;ch hợp với cả nam v&agrave; nữ. Mặc l&agrave;m &aacute;o thun cặp, &aacute;o nh&oacute;m rất ph&ugrave; hợp.<br />\n&bull; Sản phẩm 100% cotton, đường may tinh tế chắc chắn với bề mặt vải mềm mại, thấm h&uacute;t mồ h&ocirc;i tốt tạo cảm gi&aacute;c tho&aacute;ng m&aacute;t cho người mặc.<br />\n&bull; Form &aacute;o cơ bản, vừa vặn cơ thể, thoải m&aacute;i theo từng cử động.<br />\n&bull; Kh&ocirc;ng ra m&agrave;u, kh&ocirc;ng bai, kh&ocirc;ng x&ugrave;, kh&ocirc;ng b&aacute;m d&iacute;nh.<img alt=\"cool\" src=\"http://cdn.ckeditor.com/4.7.1/full/plugins/smiley/images/shades_smile.png\" style=\"height:23px; width:23px\" title=\"cool\" /></p>\n',1500000,'d98b74bd-1969-4a08-a9e9-8a5a0ce45b74.jpg','2021-05-01 00:00:00'),(2,1,'Áo polo nam','<p>&bull; L&agrave; item kh&ocirc;ng thể thiếu trong tủ đồ v&igrave; sự thoải m&aacute;i, dễ chịu, lại rất dễ phối đồ.<br />\n&bull; &Aacute;o thun unisex th&iacute;ch hợp với cả nam v&agrave; nữ. Mặc l&agrave;m &aacute;o thun cặp, &aacute;o nh&oacute;m rất ph&ugrave; hợp.<br />\n&bull; Sản phẩm 100% cotton, đường may tinh tế chắc chắn với bề mặt vải mềm mại, thấm h&uacute;t mồ h&ocirc;i tốt tạo cảm gi&aacute;c tho&aacute;ng m&aacute;t cho người mặc.<br />\n&bull; Form &aacute;o cơ bản, vừa vặn cơ thể, thoải m&aacute;i theo từng cử động.<br />\n&bull; Kh&ocirc;ng ra m&agrave;u, kh&ocirc;ng bai, kh&ocirc;ng x&ugrave;, kh&ocirc;ng b&aacute;m d&iacute;nh.</p>\n',340000,'fe4bd527-3875-45a8-8b74-453c35ad7c09.jpg','2021-05-19 15:15:24'),(3,2,'Áo Croptop nữ Marvel BOOZilla','- Áo in hình logo Marvel trước ngực',440000,'376a1c2a-a42d-44c6-b936-cc5b66ed1d79.jpg','2021-05-19 15:17:01'),(4,3,'Áo trẻ em','Chất liệu vải 100% cotton cao cấp nhất',200000,'99f029d8-e386-40ff-85a8-95035bf13dd4.jpg','2021-05-19 15:18:39'),(5,3,'Quần trẻ em','<p>H&Igrave;NH IN BẢN QUYỀN DISNEY GỒM 2 PHI&Ecirc;N BẢN<br />\nPhi&ecirc;n bản lấy cảm hứng từ bức tranh Đ&ocirc;ng Hồ nổi tiếng &ldquo;Mục đồng thổi s&aacute;o&rdquo; mang &yacute; nghĩa ca ngợi người n&ocirc;ng d&acirc;n vừa si&ecirc;ng năng lao động vừa biết s&aacute;ng tạo nghệ thuật, Canifa v&agrave; Mickey go Vietnam mang một diện mạo mới, th&acirc;n thuộc hơn. Bức tranh thể hiện tinh thần vui tươi lạc quan, gần gũi với thi&ecirc;n nhi&ecirc;n, văn h&oacute;a v&agrave; con người Việt Nam.<br />\n<br />\nPhi&ecirc;n bản ấy cảm hứng từ bức tranh Đ&ocirc;ng Hồ nổi tiếng &ldquo;Hứng dừa&rdquo; đề cao sự cảm th&ocirc;ng, chia sẻ v&agrave; gắn b&oacute; của vợ chồng, cha con để x&acirc;y dựng hạnh ph&uacute;c gia đ&igrave;nh, Mickey go Vietnam mang một &yacute; nghĩa mới về t&igrave;nh bạn th&acirc;n thiết giữa Mickey v&agrave; Minnie, Donald, Daisy khi trải nghiệm văn h&oacute;a Việt Nam.</p>\n',300000,'4e614370-d9f8-4076-83bb-e45de3ffa64a.jpg','2021-05-19 23:01:53'),(6,3,'ÁO PHÔNG BÉ TRAI COTTON USA IN HÌNH','<p>&Aacute;o ph&ocirc;ng chất liệu 100% cotton USA c&oacute; in h&igrave;nh<br />\nD&aacute;ng regular, cổ tr&ograve;n, tay cộc.</p>\n\n<p><a href=\"https://canifa.com/catalog/product/view/id/222688/s/Ao-phong-be-trai-2TS21S011/category/814/#product-detail-tab-content-2\">CHẤT LIỆU</a></p>\n\n<p>100% cotton</p>\n\n<p><a href=\"https://canifa.com/catalog/product/view/id/222688/s/Ao-phong-be-trai-2TS21S011/category/814/#product-detail-tab-content-3\">HƯỚNG DẪN SỬ DỤNG</a></p>\n\n<p>Giặt m&aacute;y ở chế độ nhẹ, nhiệt độ thường.<br />\nKh&ocirc;ng sử dụng h&oacute;a chất tẩy c&oacute; chứa Clo.<br />\nPhơi trong b&oacute;ng m&aacute;t.<br />\nSấy th&ugrave;ng, chế độ nhẹ nh&agrave;ng.<br />\nL&agrave; ở nhiệt độ trung b&igrave;nh 150 độ C.<br />\nGiặt với sản phẩm c&ugrave;ng m&agrave;u.<br />\nKh&ocirc;ng l&agrave; l&ecirc;n chi tiết trang tr&iacute;.</p>\n',400000,'b8980358-2185-42e6-b997-504592d54db2.jpg','2021-05-19 23:01:53'),(7,3,'ÁO PHÔNG BÉ GÁI IN HÌNH','